INSERT INTO disposal_guideline (id, wasteType, guideline) VALUES (1, 'Paper Waste', 'Flatten cardboard boxes before disposal to save space.');
INSERT INTO disposal_guideline (id, wasteType, guideline) VALUES (2, 'Hazardous Waste', 'Dispose of batteries and paint at authorized hazardous waste facilities.');
INSERT INTO disposal_guideline (id, wasteType, guideline) VALUES (3, 'Medical Waste', 'Place sharps (like needles) in puncture-resistant containers before disposal.');
INSERT INTO recycling_tip (id, category, tip) VALUES (1, 'Glass', 'Separate glass bottles and jars by color before recycling.');
INSERT INTO recycling_tip (id, category, tip) VALUES (2, 'Metal', 'Rinse and clean metal cans to prevent contamination in the recycling process.');
INSERT INTO recycling_tip (id, category, tip) VALUES (3, 'General', 'Educate yourself about local recycling rules to avoid contaminating recycling bins.');